jQuery(document).ready(function(){PROHOST_STORAGE.media_frame=null;PROHOST_STORAGE.media_link="";jQuery(".prohost_media_selector").on("click",function(a){prohost_show_media_manager(this);a.preventDefault();return false})});function prohost_show_media_manager(a){PROHOST_STORAGE.media_link=jQuery(a);if(PROHOST_STORAGE.media_frame){PROHOST_STORAGE.media_frame.open();return false}PROHOST_STORAGE.media_frame=wp.media({title:PROHOST_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:PROHOST_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:PROHOST_STORAGE.media_link.data("update"),close:true}});PROHOST_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+PROHOST_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(PROHOST_STORAGE.media_link.data("multiple")===true){PROHOST_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=PROHOST_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});PROHOST_STORAGE.media_frame.open();return false};